insert into attrazione (id, nome, descrizione, apertura, chiusura, prezzo, urlimage, quartiere, tipo) values (nextval('attrazione_seq'), 'Colosseo', 'Il Colosseo è uno dei monumenti più iconici di Roma. Costruito nel I secolo d.C., questo antico anfiteatro ospitava giochi gladiatori e spettacoli pubblici, e oggi è una delle attrazioni turistiche più visitate al mondo.', '09:00', '18:30', '17.00', 'images/colosseo.jpg', 'Monti', (SELECT id FROM tipologia_attrazione WHERE nome = 'monumento'));
insert into attrazione (id, nome, descrizione, apertura, chiusura, prezzo, urlimage, quartiere, tipo) values (nextval('attrazione_seq'), 'Pantheon', 'Il Pantheon, un magnifico tempio romano dedicato a tutti gli dei, è famoso per la sua cupola incredibilmente grande e il suo oculus centrale che lascia entrare la luce naturale. Costruito durante il regno di Adriano, è una meraviglia architettonica.', '09:00', '19:00', '0.00', 'images/pantheon.jpg', 'Pigna', (SELECT id FROM tipologia_attrazione WHERE nome = 'monumento'));
insert into attrazione (id, nome, descrizione, apertura, chiusura, prezzo, urlimage, quartiere, tipo) values (nextval('attrazione_seq'), 'Arco di Costantino', 'Situato vicino al Colosseo, l''Arco di Costantino è un arco di trionfo eretto per commemorare la vittoria di Costantino nella battaglia di Ponte Milvio nel 312 d.C. Decorato con rilievi e iscrizioni, è un magnifico esempio di arte romana.', '09:00', '18:00', '0.00', 'images/arco_di_costantino.jpg', 'Celio', (SELECT id FROM tipologia_attrazione WHERE nome = 'monumento'));
insert into attrazione (id, nome, descrizione, apertura, chiusura, prezzo, urlimage, quartiere, tipo) values (nextval('attrazione_seq'), 'Altare della Patria', 'L''Altare della Patria, dedicato a Vittorio Emanuele II, è un monumento imponente situato in Piazza Venezia. Conosciuto anche come Vittoriano, offre una vista panoramica sulla città e ospita il Museo del Risorgimento.', '09:30', '19:30', '0.00', 'images/altare_della_patria.jpg', 'Campitelli', (SELECT id FROM tipologia_attrazione WHERE nome = 'monumento'));
insert into attrazione (id, nome, descrizione, apertura, chiusura, prezzo, urlimage, quartiere, tipo) values (nextval('attrazione_seq'), 'Foro Romano', 'Il Foro Romano era il centro della vita pubblica nell''antica Roma, ospitando importanti edifici governativi, templi e mercati. Oggi, i resti del foro offrono uno sguardo affascinante sulla storia e l''architettura romane.', '08:30', '16:30', '12.00', 'images/foro_romano.jpg', 'Campitelli', (SELECT id FROM tipologia_attrazione WHERE nome = 'monumento'));
insert into attrazione (id, nome, descrizione, apertura, chiusura, prezzo, urlimage, quartiere, tipo) values (nextval('attrazione_seq'), 'Basilica di San Pietro', 'La Basilica di San Pietro è la principale chiesa del cattolicesimo e una delle più grandi del mondo. Situata in Vaticano, è famosa per la sua architettura grandiosa, il Baldacchino di Bernini e la Pietà di Michelangelo.', '07:00', '18:30', '0.00', 'images/basilica_san_pietro.jpg', 'Vaticano', (SELECT id FROM tipologia_attrazione WHERE nome = 'chiesa'));
insert into attrazione (id, nome, descrizione, apertura, chiusura, prezzo, urlimage, quartiere, tipo) values (nextval('attrazione_seq'), 'Basilica di San Giovanni in Laterano', 'San Giovanni in Laterano è la cattedrale di Roma e la sede ecclesiastica ufficiale del Papa. Conosciuta per il suo imponente chiostro e le statue degli apostoli, è una delle quattro basiliche papali maggiori.', '07:00', '18:00', '0.00', 'images/san_giovanni_laterano.jpg', 'Laterano', (SELECT id FROM tipologia_attrazione WHERE nome = 'chiesa'));
insert into attrazione (id, nome, descrizione, apertura, chiusura, prezzo, urlimage, quartiere, tipo) values (nextval('attrazione_seq'), 'Basilica di Santa Maria Maggiore', 'Una delle quattro basiliche maggiori di Roma, Santa Maria Maggiore è famosa per i suoi splendidi mosaici del V secolo, il soffitto dorato e la Cappella Sistina, che ospita il presepe del XIII secolo di Arnolfo di Cambio.', '07:00', '19:00', '0.00', 'images/santa_maria_maggiore.jpg', 'Esquilino', (SELECT id FROM tipologia_attrazione WHERE nome = 'chiesa'));
insert into attrazione (id, nome, descrizione, apertura, chiusura, prezzo, urlimage, quartiere, tipo) values (nextval('attrazione_seq'), 'Basilica di San Paolo fuori le Mura', 'San Paolo fuori le Mura è una delle quattro basiliche papali di Roma. Costruita sopra la tomba dell''apostolo Paolo, la basilica è famosa per il suo chiostro medievale e i mosaici bizantini.', '07:00', '18:30', '0.00', 'images/san_paolo_fuori_le_mura.jpg', 'Ostiense', (SELECT id FROM tipologia_attrazione WHERE nome = 'chiesa'));
insert into attrazione (id, nome, descrizione, apertura, chiusura, prezzo, urlimage, quartiere, tipo) values (nextval('attrazione_seq'), 'Basilica di Santa Maria in Trastevere', 'Una delle chiese più antiche di Roma, Santa Maria in Trastevere è conosciuta per i suoi splendidi mosaici del XII secolo che decorano l''abside e la facciata. È un luogo di culto vivace nel cuore del quartiere di Trastevere.', '07:30', '21:00', '0.00', 'images/santa_maria_trastevere.jpg', 'Trastevere', (SELECT id FROM tipologia_attrazione WHERE nome = 'chiesa'));
insert into attrazione (id, nome, descrizione, apertura, chiusura, prezzo, urlimage, quartiere, tipo) values (nextval('attrazione_seq'), 'Musei Vaticani', 'I Musei Vaticani, una delle raccolte d''arte più importanti al mondo, ospitano opere di artisti come Michelangelo e Raffaello. Il percorso culmina nella Cappella Sistina, famosa per gli affreschi di Michelangelo.', '09:00', '18:00', '17.00', 'images/musei_vaticani.jpg', 'Vaticano', (SELECT id FROM tipologia_attrazione WHERE nome = 'museo'));
insert into attrazione (id, nome, descrizione, apertura, chiusura, prezzo, urlimage, quartiere, tipo) values (nextval('attrazione_seq'), 'Galleria Borghese', 'La Galleria Borghese, situata nel cuore di Villa Borghese, è un museo d’arte che espone capolavori di Caravaggio, Bernini, Tiziano e altri. Ogni sala è un gioiello, con sculture e dipinti che raccontano la storia dell''arte italiana.', '09:00', '19:00', '13.00', 'images/galleria_borghese.jpg', 'Pinciano', (SELECT id FROM tipologia_attrazione WHERE nome = 'museo'));
insert into attrazione (id, nome, descrizione, apertura, chiusura, prezzo, urlimage, quartiere, tipo) values (nextval('attrazione_seq'), 'Museo Nazionale Romano', 'Il Museo Nazionale Romano è dedicato all''antichità romana, con una vasta collezione di sculture, affreschi e manufatti che raccontano la storia di Roma dall''età repubblicana a quella imperiale.', '09:00', '19:45', '10.00', 'images/museo_nazionale_romano.jpg', 'Castro Pretorio', (SELECT id FROM tipologia_attrazione WHERE nome = 'museo'));
insert into attrazione (id, nome, descrizione, apertura, chiusura, prezzo, urlimage, quartiere, tipo) values (nextval('attrazione_seq'), 'Galleria d''Arte Moderna', 'La Galleria d''Arte Moderna di Roma ospita una collezione di opere del XIX e XX secolo, con dipinti, sculture e installazioni di artisti italiani e internazionali. È un luogo ideale per esplorare l''arte moderna e contemporanea.', '10:00', '18:30', '10.00', 'images/galleria_arte_moderna.jpg', 'Flaminio', (SELECT id FROM tipologia_attrazione WHERE nome = 'museo'));
insert into attrazione (id, nome, descrizione, apertura, chiusura, prezzo, urlimage, quartiere, tipo) values (nextval('attrazione_seq'), 'Museo MAXXI', 'Il Museo MAXXI, progettato da Zaha Hadid, è il primo museo nazionale dedicato all''arte del XXI secolo in Italia. Ospita mostre di arte contemporanea, architettura e design, ed è un punto di riferimento per l''innovazione culturale.', '11:00', '19:00', '12.00', 'images/museo_maxxi.jpg', 'Flaminio', (SELECT id FROM tipologia_attrazione WHERE nome = 'museo'));
insert into ristorante (id, nome, descrizione, apertura, chiusura, urlimage, indirizzo, tipo, quartiere) values (nextval('ristorante_seq'), 'La Taverna dei Monti', 'Un accogliente ristorante nel cuore del quartiere Monti, che offre piatti tradizionali romani con un tocco moderno.', '12:00', '23:00', 'images/taverna_dei_monti.jpg', 'Via del Boschetto, 41', 'tradizionale', 'Monti');
insert into ristorante (id, nome, descrizione, apertura, chiusura, urlimage, indirizzo, tipo, quartiere) values (nextval('ristorante_seq'), 'Osteria della Suburra', 'Un luogo storico a Monti, conosciuto per la sua cucina autentica romana e l''atmosfera rustica.', '12:30', '22:30', 'images/osteria_suburra.jpg', 'Via Urbana, 67', 'tradizionale', 'Monti');
insert into ristorante (id, nome, descrizione, apertura, chiusura, urlimage, indirizzo, tipo, quartiere) values (nextval('ristorante_seq'), 'Ristorante il Pantheon', 'Elegante ristorante situato vicino al Pantheon, famoso per la sua cucina italiana raffinata e l''ambiente romantico.', '12:00', '23:00', 'images/ristorante_pantheon.jpg', 'Piazza della Rotonda, 8', 'elegante', 'Pigna');
insert into ristorante (id, nome, descrizione, apertura, chiusura, urlimage, indirizzo, tipo, quartiere) values (nextval('ristorante_seq'), 'Trattoria Da Gino', 'Una trattoria tradizionale che serve piatti classici romani in un ambiente accogliente e familiare.', '12:00', '22:00', 'images/trattoria_da_gino.jpg', 'Via della Maddalena, 4', 'tradizionale', 'Pigna');
insert into ristorante (id, nome, descrizione, apertura, chiusura, urlimage, indirizzo, tipo, quartiere) values (nextval('ristorante_seq'), 'Ristorante Vittoriano', 'Situato vicino all''Altare della Patria, questo ristorante offre piatti italiani di alta qualità in un contesto elegante.', '12:00', '22:00', 'images/ristorante_vittoriano.jpg', 'Piazza Venezia, 1', 'elegante', 'Campitelli');
